/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `AmsterdamTeam9`.`MatchUp_AFTER_INSERT` AFTER INSERT ON `MatchUp` FOR EACH ROW
BEGIN

DELETE FROM AmsterdamTeam9.Result WHERE Match_ID = NEW.Match_ID;

INSERT INTO 
	AmsterdamTeam9.Result (Match_ID, RacksWon, RacksLost, MatchUpsWon, MatchUpsLost, DidWeWin)
SELECT 
    u.Match_ID,
    SUM(u.MyPlayerScore) AS RacksWon,
    SUM(u.OpponentScore) AS RacksLost,
    COUNT(IF(WinLose = 'W', 1, NULL)) AS MatchUpsWon,
    COUNT(IF(WinLose = 'L', 1, NULL)) AS MatchUpsLost,
    IF((((COUNT(IF(WinLose = 'W', 1, NULL)) > COUNT(IF(WinLose = 'L', 1, NULL)))
            OR (COUNT(IF(WinLose = 'W', 1, NULL)) = COUNT(IF(WinLose = 'L', 1, NULL))
            AND SUM(u.MyPlayerScore) > SUM(u.OpponentScore)))
            AND COUNT(*) > 3),
        1,
        0) AS DidWeWin
FROM
    AmsterdamTeam9.MatchUp AS u
WHERE
    u.Match_ID = NEW.Match_ID
GROUP BY u.Match_ID;

UPDATE AmsterdamTeam9.Player p,
(SELECT MatchDate from AmsterdamTeam9.Match WHERE idmatch = NEW.Match_ID) m
SET p.Bogged = if(NEW.WinLose = 'L', 'Y', 'N'), p.BoggedDate = if(NEW.WinLose = 'L', m.MatchDate, p.BoggedDate)
WHERE p.idplayer = NEW.Player_ID;

END */;;
